import React, { Component } from "react";
import { Route, Navigate } from "react-router-dom";
import { isAuth } from "./helper";




const PrivateRoute = ({ component: Component, ...rest }) => (
    <Route
        {...rest}
        render={(props) =>
            isAuth() ? (
                <Component {...props} />
            ) : (
                <Navigate
                    replace
                    to={{
                        pathname: "/signin",
                        state: { from: props.location },
                    }}
                />
            )
        }
    />
);

export default PrivateRoute;
